(() => {
    "use strict";
    const t = function () {
        var t = document.querySelector(".spots_map"),
            e = [
                {x:32,y:15,content:"Hà Nội"},
                {x:43,y:46,content:"Đà Nẵng"},
                {x:36,y:77,content:"TP. Hồ Chí Minh"}
            ];
        function n() {
            var t = document.querySelectorAll(".hot-spot"),
                n = document.querySelectorAll(".speech-bubble");
            t.forEach(function (t, o) {
                var c = e[o].x,
                    a = e[o].y,
                    s = e[o].content;
                (t.style.top = "".concat(a, "%")),
                    (t.style.left = "".concat(c, "%")),
                    (n[o].textContent = s),
                    window.innerWidth >= 992
                        ? ((n[o].style.top = "".concat(a, "%")), (n[o].style.left = "".concat(c, "%")))
                        : ((n[o].style.top = "50%"), (n[o].style.left = "50%")),
                    document.body.addEventListener("click", function (e) {
                        e.target === t ? n[o].classList.add("active") : n[o].classList.remove("active");
                    });
            });
        }
        !(function () {
            for (var o = 0; o < e.length; o++) {
                var c = document.createElement("div");
                c.classList.add("hot-spot"), t.append(c);
                var a = document.createElement("span");
                a.classList.add("speech-bubble"), t.append(a);
            }
            n();
        })(),
            window.addEventListener("load", n),
            window.addEventListener("resize", n);
    };
    document.addEventListener("DOMContentLoaded", function () {
        t();
    });
})();
